interface IInGameItems {
    struct Item {
        uint itemId;
        uint itemType;
        uint slots;
    }

    struct ItemSeries {
        uint itemId;
        uint itemSeriesId;
        uint startingTokenId;
        uint editionSize;
        uint minted;
